using ADOX;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Connection string to your Access database\n        string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path\\to\\your\\database.mdb";\n\n        // Initialize ADOX Catalog object\n        Catalog cat = new Catalog();\n        cat.ActiveConnection = connectionString;\n\n        // Assuming you're looking for a specific table\n        string tableName = "YourTableName";\n\n        // Iterate through tables to find the one we're interested in\n        foreach (Table table in cat.Tables)\n        {\n            if (table.Name == tableName)\n            {\n                // Now, iterate through the keys to find the primary key (or other constraints)\n                foreach (ADOX.Key key in table.Keys)\n                {\n                    // Check if it's the primary key\n                    if (key.Type == KeyTypeEnum.adKeyPrimary)\n                    {\n                        // Print or store the name of the primary key constraint\n                        Console.WriteLine("Primary Key Name: " + key.Name);\n                    }\n                }\n            }\n        }\n\n        // Clean up\n        cat.ActiveConnection = null;\n        cat = null;\n    }\n}