var result = context.Collect.GroupBy(c => new { c.Url, c.CollectId, c.UserId, c.PubTime })\n                            .Having(g => g.Key.PubTime >= context.Collect.Where(d => d.Url == g.Key.Url).Max(d => d.PubTime))\n                            .OrderByDescending(g => g.Key.PubTime)\n                            .Select(g => new { g.Key.CollectId, g.Key.Url, g.Key.UserId, g.Key.PubTime })\n                            .ToList();