var result = (from c in context.Collect\n              group c by new { c.Url, c.CollectId, c.UserId, c.PubTime } into g\n              where g.Key.PubTime >= (from d in context.Collect\n                                      where d.Url == g.Key.Url\n                                      select d.PubTime).Max()\n              orderby g.Key.PubTime descending\n              select new { g.Key.CollectId, g.Key.Url, g.Key.UserId, g.Key.PubTime }).ToList();