protected void CustomValidator1_ServerValidate(object source, ServerValidateEventArgs args)\n{\n    // Get the text box value\n    string textBoxValue = ((TextBox)source).Text;\n\n    // Get all other text boxes\n    var otherTextBoxes = Page.Controls.OfType<TextBox>().Where(t => t != (TextBox)source);\n\n    // Check if any other text box has the same value\n    bool isUnique = !otherTextBoxes.Any(t => t.Text == textBoxValue);\n\n    args.IsValid = isUnique;\n}