using System;\nusing System.Reflection;\n\n// Add an event handler for AssemblyResolve\nAppDomain.CurrentDomain.AssemblyResolve += (sender, args) =>\n{\n    if (args.Name.StartsWith("Oracle.DataAccess"))\n    {\n        string assemblyPath = Path.Combine("oracle_client", "Oracle.DataAccess.dll");\n        return Assembly.LoadFrom(assemblyPath);\n    }\n    return null;\n};\n\n// Create an OracleConnection instance\nOracleConnection conn = new OracleConnection("your_connection_string");