using System;\nusing System.Net.Http;\nusing System.Text;\nusing Newtonsoft.Json;\n\npublic class EmailVerificationResponse\n{\n    public bool Valid { get; set; }\n}\n\npublic class EmailVerificationService\n{\n    private readonly HttpClient _httpClient;\n\n    public EmailVerificationService(string apiKey)\n    {\n        _httpClient = new HttpClient();\n        _httpClient.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", apiKey);\n    }\n\n    public async Task<bool> VerifyEmailAsync(string email)\n    {\n        var request = new HttpRequestMessage(HttpMethod.Get, $"https://person.clearbit.com/v2/combined/find?email={email}");\n        var response = await _httpClient.SendAsync(request);\n\n        if (response.IsSuccessStatusCode)\n        {\n            var json = await response.Content.ReadAsStringAsync();\n            var verificationResponse = JsonConvert.DeserializeObject<EmailVerificationResponse>(json);\n            return verificationResponse.Valid;\n        }\n\n        return false;\n    }\n}