string xmlData = "<root><person><name>John</name><age>30</age></person></root>";\nXDocument xmlDoc = XDocument.Parse(xmlData);\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    foreach (XElement person in xmlDoc.Descendants("person"))\n    {\n        string name = person.Element("name").Value;\n        int age = int.Parse(person.Element("age").Value);\n\n        SqlCommand command = new SqlCommand("INSERT INTO XmlTable (Name, Age) VALUES (@name, @age)", connection);\n        command.Parameters.AddWithValue("@name", name);\n        command.Parameters.AddWithValue("@age", age);\n        command.ExecuteNonQuery();\n    }\n}