using System.Windows;\nusing System.Windows.Media;\n\n// Get the actual width and height of the canvas in DIU's\ndouble canvasWidth = canvas.ActualWidth;\ndouble canvasHeight = canvas.ActualHeight;\n\n// Get the DPI of the primary monitor\nPresentationSource source = PresentationSource.FromVisual(canvas);\ndouble dpiX = 96;\ndouble dpiY = 96;\nif (source != null)\n{\n    dpiX = source.CompositionTarget.TransformToDevice.M11 * 96;\n    dpiY = source.CompositionTarget.TransformToDevice.M22 * 96;\n}\n\n// Convert DIU's to inches\ndouble canvasWidthInInches = canvasWidth / dpiX;\ndouble canvasHeightInInches = canvasHeight / dpiY;\n\n// Now you can calculate the canvas size in meters\ndouble canvasWidthInMeters = canvasWidthInInches * 0.0254; // 1 inch = 0.0254 meters\ndouble canvasHeightInMeters = canvasHeightInInches * 0.0254;