using System;\nusing System.Runtime.InteropServices;\n\nclass AeroGlassEffect\n{\n    [DllImport("dwmapi.dll")]\n    static extern int DwmExtendFrameIntoClientArea(IntPtr hWnd, ref MARGINS margins);\n\n    [StructLayout(LayoutKind.Sequential)]\n    public struct MARGINS\n    {\n        public int cxLeftWidth;\n        public int cxRightWidth;\n        public int cyTopHeight;\n        public int cyBottomHeight;\n    }\n\n    public static void ExtendFrameIntoClientArea(IntPtr hWnd, int left, int right, int top, int bottom)\n    {\n        MARGINS margins = new MARGINS();\n        margins.cxLeftWidth = left;\n        margins.cxRightWidth = right;\n        margins.cyTopHeight = top;\n        margins.cyBottomHeight = bottom;\n\n        DwmExtendFrameIntoClientArea(hWnd, ref margins);\n    }\n}