public struct CategoryWeight\n{\n    public string Category { get; set; }\n    public int Weight { get; set; }\n}\n\n// Load data from database into a dictionary\nDictionary<int, CategoryWeight> lookupTable = new Dictionary<int, CategoryWeight>();\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    SqlCommand command = new SqlCommand("SELECT ID, Category, Weight FROM YourTable", connection);\n    SqlDataReader reader = command.ExecuteReader();\n\n    while (reader.Read())\n    {\n        int id = reader.GetInt32(0);\n        string category = reader.GetString(1);\n        int weight = reader.GetInt32(2);\n\n        lookupTable[id] = new CategoryWeight { Category = category, Weight = weight };\n    }\n}\n\n// Example usage:\nint idToLookup = 2;\nif (lookupTable.TryGetValue(idToLookup, out CategoryWeight cw))\n{\n    Console.WriteLine($"Category: {cw.Category}, Weight: {cw.Weight}");\n}