DataTable dt = dataset.Tables["A"];\n\n// Create a new DataTable with only the desired columns\nDataTable newDt = new DataTable();\nnewDt.Columns.Add("a3", typeof(string));\nnewDt.Columns.Add("a4", typeof(string));\n\n// Copy the data from the original DataTable to the new DataTable\nforeach (DataRow row in dt.Rows)\n{\n    newDt.Rows.Add(row["a3"], row["a4"]);\n}\n\n// Bind the new DataTable to your DataGrid\ndataGrid.DataSource = newDt;\ndataGrid.DataBind();