private DataTable dtDevice;\n\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    string query = "SELECT DeviceSerialNumber, BuildID, LEMSCredentialsID, LEMSSoftwareID, OwnerID, RestagedDate INTO #TempTable FROM tblDevice WHERE (DeviceSerialNumber = @SerialNumber)";\n    SqlCommand command = new SqlCommand(query, connection);\n    command.Parameters.AddWithValue("@SerialNumber", txtDeviceSerial.Text);\n\n    command.ExecuteNonQuery();\n\n    query = "SELECT * FROM #TempTable";\n    command = new SqlCommand(query, connection);\n\n    dtDevice = new DataTable();\n    dtDevice.Load(command.ExecuteReader());\n}