using System;\nusing System.IO;\nusing System.Reflection;\nusing Microsoft.Win32;\n\npublic class ComFileVersion\n{\n    public static string GetFileVersion(string progId)\n    {\n        // Open the registry key for the COM DLL\n        RegistryKey key = Registry.ClassesRoot.OpenSubKey(@"CLSID\" + progId + @"\InprocServer32");\n\n        // Get the file path from the registry key\n        string filePath = (string)key.GetValue("");\n\n        // Get the file version information\n        FileVersionInfo fileInfo = FileVersionInfo.GetVersionInfo(filePath);\n\n        return fileInfo.FileVersion;\n    }\n}