using System;\nusing System.ComponentModel;\n\npublic class DirtyFlagExtender : Component, IExtenderProvider\n{\n    public bool CanExtend(object extendee)\n    {\n        return extendee is TextBox;\n    }\n\n    public bool GetDirtyFlag(TextBox control)\n    {\n        // Get the dirty flag value from the control's tag\n        return (bool)control.Tag;\n    }\n\n    public void SetDirtyFlag(TextBox control, bool value)\n    {\n        // Set the dirty flag value in the control's tag\n        control.Tag = value;\n    }\n}