amespace YourNamespace\n   {\n       public class ImageHandler : IHttpHandler\n       {\n           public bool IsReusable\n           {\n               get { return false; }\n           }\n\n           public void ProcessRequest(HttpContext context)\n           {\n               string userID = context.Request.QueryString["UserID"];\n               byte[] imageBytes = null;\n               using (SqlConnection connection = new SqlConnection("YourConnectionString"))\n               {\n                   connection.Open();\n                   SqlCommand command = new SqlCommand("SELECT ProfilePicture FROM UserProfile WHERE UserID = @UserID", connection);\n                   command.Parameters.AddWithValue("@UserID", userID);\n                   SqlDataReader reader = command.ExecuteReader();\n                   if (reader.Read())\n                   {\n                       imageBytes = (byte[])reader[0];\n                   }\n                   reader.Close();\n               }\n               if (imageBytes != null)\n               {\n                   context.Response.ContentType = "image/jpeg"; // Assuming default is JPEG, adjust according to file type stored.\n                   context.Response.OutputStream.Write(imageBytes, 0, imageBytes.Length);\n               }\n               else\n               {\n                   context.Response.StatusCode = 404; // Not Found\n               }\n               context.Response.End();\n           }\n       }\n   }