using System.Web.Configuration;\n\npublic class MachineKeyConfigurator\n{\n    public void ConfigureMachineKey()\n    {\n        MachineKeySection machineKeySection = (MachineKeySection)WebConfigurationManager.GetSection("system.web/machineKey");\n        machineKeySection.ValidationKey = "your_unique_validation_key_here";\n        machineKeySection.DecryptionKey = "your_unique_decryption_key_here";\n        machineKeySection.ValidationAlgorithm = "HMACSHA256";\n        machineKeySection.DecryptionAlgorithm = "AES";\n    }\n}