[ComVisible(true)]\n[InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\npublic interface IMyClass\n{\n    string GetSpecialCookieValue(IServer server);\n}\n\n[ComVisible(true)]\n[ClassInterface(ClassInterfaceType.AutoDispatch)]\npublic class MyClass : IMyClass\n{\n    public string GetSpecialCookieValue(IServer server)\n    {\n        // Get the HttpContext instance from the IServer object\n        var context = new HttpContext(server.GetContext());\n\n        // Access the cookie information\n        var cookieValue = context.Request.Cookies["myCookie"].Value;\n\n        return cookieValue;\n    }\n}