using System;\nusing System.Text;\n\npublic class EncodingChecker\n{\n    public static bool CanHandleAllChars(Encoding encoding, string text)\n    {\n        try\n        {\n            int byteCount = encoding.GetByteCount(text);\n            return byteCount == text.Length;\n        }\n        catch (EncoderFallbackException)\n        {\n            return false;\n        }\n    }\n}