using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program {\n    static void Main(string[] args) {\n        // Create a DES crypto service provider\n        DESCryptoServiceProvider des = new DESCryptoServiceProvider();\n\n        // Create a key and IV\n        byte[] key = new byte[8];\n        byte[] iv = new byte[8];\n        des.Key = key;\n        des.IV = iv;\n\n        // Encrypt data\n        string data = "Hello, World!";\n        byte[] encryptedData = Encrypt(data, des);\n\n        // Decrypt data\n        string decryptedData = Decrypt(encryptedData, des);\n\n        Console.WriteLine(decryptedData);\n    }\n\n    static byte[] Encrypt(string data, DESCryptoServiceProvider des) {\n        byte[] bytes = Encoding.UTF8.GetBytes(data);\n        return des.Encrypt(bytes);\n    }\n\n    static string Decrypt(byte[] encryptedData, DESCryptoServiceProvider des) {\n        byte[] decryptedBytes = des.Decrypt(encryptedData);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n}