[DllImport("user32.dll")]\npublic static extern int ShowWindow(IntPtr handle, int nCmdShow);\n\n[DllImport("user32.dll")]\npublic static extern int SetForegroundWindow(IntPtr handle);\n\n// Example usage:\nIntPtr hWnd = // get the handle to the window you want to manipulate\n\n// Show the window\nShowWindow(hWnd, 1); // SW_SHOWNORMAL = 1\n\n// Bring the window to the front\nSetForegroundWindow(hWnd);