// Get the user's time zone\nTimeZoneInfo userTimeZone = TimeZoneInfo.FindSystemTimeZoneById("America/New_York");\n\n// Get the UTC date/time value from the database\nDateTime utcValue = DateTime.UtcNow;\n\n// Convert the UTC value to the user's local time\nDateTime localValue = TimeZoneInfo.ConvertTimeFromUtc(utcValue, userTimeZone);\n\n// Display the local time to the user\nConsole.WriteLine(localValue);