public struct Point\n{\n    public double X { get; set; }\n    public double Y { get; set; }\n}\n\npublic List<Point> FindAllPointsInLine(Point start, Point end, double minDistanceApart)\n{\n    List<Point> points = new List<Point>();\n\n    // Calculate the length of the line segment\n    double length = Math.Sqrt(Math.Pow(end.X - start.X, 2) + Math.Pow(end.Y - start.Y, 2));\n\n    // Calculate the maximum number of points\n    int maxPoints = (int)(length / minDistanceApart);\n\n    // Calculate the coordinates of each point\n    for (int i = 0; i <= maxPoints; i++)\n    {\n        double t = (double)i / maxPoints;\n        Point point = new Point\n        {\n            X = start.X + (end.X - start.X) * t,\n            Y = start.Y + (end.Y - start.Y) * t\n        };\n        points.Add(point);\n    }\n\n    return points;\n}