using Renci.SshNet;\n\nclass SftpUploadExample\n{\n    public void UploadFile(string filePath, string sftpServer, string username, string password)\n    {\n        using (var sftpClient = new SftpClient(sftpServer, username, password))\n        {\n            sftpClient.Connect();\n            sftpClient.UploadFile(filePath, "/remote/path");\n            sftpClient.Disconnect();\n        }\n    }\n}