using PeNet;\n\npublic static MachineType GetDllArchitecture(string dllPath)\n{\n    var peFile = new PeFile(dllPath);\n    return peFile.ImageNtHeaders.FileHeader.Machine;\n}\n\npublic enum MachineType : ushort\n{\n    IMAGE_FILE_MACHINE_I386 = 0x014c, // x86\n    IMAGE_FILE_MACHINE_AMD64 = 0x8664, // x64\n    // ... other architectures\n}\n\n// Usage:\nvar dllPath = "path/to/your/dll.dll";\nvar architecture = GetDllArchitecture(dllPath);\nif (architecture == MachineType.IMAGE_FILE_MACHINE_I386)\n{\n    Console.WriteLine("DLL is x86");\n}\nelse if (architecture == MachineType.IMAGE_FILE_MACHINE_AMD64)\n{\n    Console.WriteLine("DLL is x64");\n}