using System.Text.RegularExpressions;\n\n// Validate view name\nstring viewName = "foo";\nif (!Regex.IsMatch(viewName, @"^[a-zA-Z_][a-zA-Z0-9_]*$"))\n{\n    throw new ArgumentException("Invalid view name");\n}\n\n// Validate view body\nstring viewBody = "* from bar";\nif (Regex.IsMatch(viewBody, @"--;|/\*|\*/|EXEC|INSERT|UPDATE|DELETE|DROP|CREATE|ALTER|TRUNCATE"))\n{\n    throw new ArgumentException("Invalid view body");\n}\n\n// Build the CREATE VIEW statement\nstring createViewStatement = $"CREATE VIEW {viewName} AS SELECT {viewBody}";\n\n// Execute the statement\nSqlCommand cmd = new SqlCommand(createViewStatement);