using System;\nusing System.Collections.Generic;\n\npublic class WindowsVersion\n{\n    private static Dictionary<string, string> versionMap = new Dictionary<string, string>()\n    {\n        { "6.0.6001", "Windows Vista Ultimate Edition" },\n        { "6.1.7601", "Windows 7 Ultimate" },\n        { "6.2.9200", "Windows 8 Pro" },\n        { "6.3.9600", "Windows 8.1 Pro" },\n        { "10.0.10240", "Windows 10 Home" },\n        { "10.0.10586", "Windows 10 Pro" },\n        // Add more version numbers and product names as needed\n    };\n\n    public static string GetWindowsVersion()\n    {\n        var osVersion = Environment.OSVersion;\n        var versionString = $"{osVersion.Version.Major}.{osVersion.Version.Minor}.{osVersion.Version.Build}";\n        return versionMap.ContainsKey(versionString) ? versionMap[versionString] : "Unknown";\n    }\n}