protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if (FileUploadControl.HasFile)\n    {\n        try\n        {\n            // Ensure the file is a valid image\n            string fileExtension = System.IO.Path.GetExtension(FileUploadControl.FileName).ToLower();\n            if (fileExtension == ".jpg" || fileExtension == ".jpeg" || fileExtension == ".gif" || fileExtension == ".png")\n            {\n                byte[] imageData = FileUploadControl.FileBytes;\n                \n                using (SqlConnection connection = new SqlConnection("YourConnectionString"))\n                {\n                    connection.Open();\n                    SqlCommand command = new SqlCommand("UPDATE UserProfile SET ProfilePicture = @Image WHERE UserID = @UserID", connection);\n                    command.Parameters.AddWithValue("@UserID", UserID); // Make sure to replace UserID with the actual user ID\n                    command.Parameters.AddWithValue("@Image", imageData);\n                    command.ExecuteNonQuery();\n                }\n            }\n            else\n            {\n                // Handle invalid file types\n                StatusLabel.Text = "Only JPEG, GIF, and PNG files are allowed.";\n            }\n        }\n        catch (Exception ex)\n        {\n            // Handle any exceptions\n            StatusLabel.Text = "The file could not be uploaded. The following error occurred: " + ex.Message;\n        }\n    }\n}