using Microsoft.SharePoint;\n\nSPSite site = new SPSite("http://your-sharepoint-site");\nSPWeb web = site.OpenWeb();\nSPList list = web.Lists["Your Survey Name"];\nSPView view = list.Views.Add("External User View", "Only show items created by the user", SPViewCollection.SPViewType.Html, false);\nview.Query = "<Where><Eq><FieldRef Name='Author'/><Value Type='Integer'><UserID/></Value></Eq></Where>";\nview.Update();