using System;\nusing System.Net;\nusing System.Xml;\n\nnamespace SharePointPermissions\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string siteUrl = "http://your-sharepoint-site";\n            string listName = "YourListName";\n            string username = "username";\n\n            // Create a new instance of the Lists web service\n            Lists lists = new Lists();\n            lists.Url = siteUrl + "/_vti_bin/Lists.asmx";\n            lists.Credentials = CredentialCache.DefaultCredentials;\n\n            // Get the ID of the list\n            XmlNode listNode = lists.GetListCollection().ChildNodes[0];\n            string listId = listNode.Attributes["ID"].Value;\n\n            // Get the ID of the user\n            XmlNode userNode = lists.GetUserInfo(username);\n            string userId = userNode.Attributes["ID"].Value;\n\n            // Get the permissions of the user for the list\n            XmlNode permissionsNode = lists.GetUserEffectivePermissions(listId, userId);\n            XmlNode permissions = permissionsNode.SelectSingleNode("Permissions");\n\n            // Check if the user has a specific permission\n            string permissionName = "EditListItems";\n            XmlNode permissionNode = permissions.SelectSingleNode(permissionName);\n            if (permissionNode != null)\n            {\n                Console.WriteLine("User has permission: " + permissionName);\n            }\n            else\n            {\n                Console.WriteLine("User does not have permission: " + permissionName);\n            }\n        }\n    }\n}