Label label1 = (Label)Page.FindControl("label1");\nif (label1 == null)\n{\n    // if label1 is not found directly under the page, \n    // recursively search for it in all controls\n    label1 = (Label)FindControlRecursive(Page, "label1");\n}\n\n//...\n\nprivate Control FindControlRecursive(Control root, string id)\n{\n    if (root.ID == id)\n    {\n        return root;\n    }\n\n    foreach (Control c in root.Controls)\n    {\n        Control t = FindControlRecursive(c, id);\n        if (t != null)\n        {\n            return t;\n        }\n    }\n\n    return null;\n}