using System;\nusing System.Runtime.Serialization.Formatters.Binary;\n\npublic class SessionSizeEstimator\n{\n    public static int EstimateSize(object obj)\n    {\n        using (var ms = new System.IO.MemoryStream())\n        {\n            var formatter = new BinaryFormatter();\n            formatter.Serialize(ms, obj);\n            return (int)ms.Length;\n        }\n    }\n}\n\n// Example usage:\nvar sessionObject = new YourSessionObject();\nvar size = SessionSizeEstimator.EstimateSize(sessionObject);\nConsole.WriteLine($"Estimated session size: {size} bytes");