using HtmlAgilityPack;\n\n// Create an HTML document\nHtmlDocument doc = new HtmlDocument();\n\n// Create a table\nHtmlTable table = doc.CreateElement("table");\ntable.Attributes.Add("border", "1");\n\n// Add rows to the table\nforeach (Item item in items)\n{\n  HtmlTableRow row = table.CreateElement("tr");\n  row.AppendChild(doc.CreateElement("td").InnerText(item.StartDate));\n  row.AppendChild(doc.CreateElement("td").InnerText(item.Id));\n  // ...\n  table.AppendChild(row);\n}\n\n// Get the HTML report\nstring report = doc.DocumentNode.OuterHtml;