using (var context = new MyDataContext())\n{\n    var query = context.ExecuteQuery<SqlDataReader>("SELECT Body FROM Documents WHERE Id = @id", new SqlParameter("@id", 1));\n\n    using (var reader = query.GetEnumerator())\n    {\n        while (reader.Read())\n        {\n            var stream = new MemoryStream();\n            var buffer = new byte[4096];\n            var bytesRead = 0;\n\n            while ((bytesRead = reader.GetBytes(0, 0, buffer, 0, buffer.Length)) > 0)\n            {\n                stream.Write(buffer, 0, bytesRead);\n            }\n\n            // Do something with the stream\n        }\n    }\n}