using Renci.SshNet;\n\n// Create a new SFTP client\nusing (var sftpClient = new SftpClient("ftp.example.com", 22, "username", "password"))\n{\n    // Connect to the SFTP server\n    sftpClient.Connect();\n\n    // Upload a file\n    using (var fileStream = new FileStream("localfile.txt", FileMode.Open))\n    {\n        sftpClient.UploadFile(fileStream, "/remote/path/file.txt");\n    }\n\n    // Download a file\n    using (var fileStream = new FileStream("localfile.txt", FileMode.Create))\n    {\n        sftpClient.DownloadFile("/remote/path/file.txt", fileStream);\n    }\n\n    // Disconnect from the SFTP server\n    sftpClient.Disconnect();\n}