[Flags()]\npublic enum CheckType\n{\n    Form = 1,\n    QueryString = 2,\n    TempData = 4,\n}\n\npublic void ProcessCheckType(CheckType checkType)\n{\n    foreach (CheckType flag in Enum.GetValues(typeof(CheckType)))\n    {\n        if ((checkType & flag) != 0)\n        {\n            // Process the individual flag\n            ProcessFlag(flag);\n        }\n    }\n}\n\nprivate void ProcessFlag(CheckType flag)\n{\n    switch (flag)\n    {\n        case CheckType.Form:\n            DoSomething(/*Some type of collection is passed */);\n            break;\n        case CheckType.QueryString:\n            DoSomethingElse(/*Some other type of collection is passed */);\n            break;\n        case CheckType.TempData:\n            DoWhatever(/*Some different type of collection is passed */);\n            break;\n    }\n}