using System.Data.SqlClient;\n\n// Connect to your database\nstring connectionString = "Your connection string";\nSqlConnection connection = new SqlConnection(connectionString);\nconnection.Open();\n\n// Get the list of stored procedures\nSqlCommand command = new SqlCommand("SELECT name FROM sys.procedures", connection);\nSqlDataReader reader = command.ExecuteReader();\n\n// Loop through the stored procedures and generate the wrapper classes\nwhile (reader.Read())\n{\n    string procedureName = reader["name"].ToString();\n\n    // Get the stored procedure parameters\n    SqlCommand parameterCommand = new SqlCommand("SELECT parameter_name, data_type FROM sys.parameters WHERE object_id = OBJECT_ID(@procedureName)", connection);\n    parameterCommand.Parameters.AddWithValue("@procedureName", procedureName);\n    SqlDataReader parameterReader = parameterCommand.ExecuteReader();\n\n    // Generate the wrapper class\n    string wrapperClass = GenerateWrapperClass(procedureName, parameterReader);\n\n    // Save the wrapper class to a file\n    File.WriteAllText(procedureName + ".cs", wrapperClass);\n}\n\n// Close the connections\nreader.Close();\nconnection.Close();