string input = HttpContext.Current.Request["key"];\nif (!string.IsNullOrEmpty(input))\n{\n    string[] ids = input.Split(',');\n\n    // Create a parameterized query\n    string sql = "DELETE FROM your_table WHERE msgId IN (@ids)";\n\n    // Create a command with a parameter\n    using (SqlCommand cmd = new SqlCommand(sql, your_connection))\n    {\n        cmd.Parameters.AddWithValue("@ids", string.Join(",", ids.Select(id => long.Parse(id))));\n        cmd.ExecuteNonQuery();\n    }\n}