using MailKit.Net.Imap;\nusing MailKit.Search;\nusing MailKit.Security;\nusing System;\n\nclass ImapClientExample\n{\n    public static void CheckForNewEmails()\n    {\n        // Gmail IMAP settings\n        string imapServer = "imap.gmail.com";\n        int port = 993;\n        string username = "your_email_address";\n        string password = "your_password";\n\n        // Create a new IMAP client\n        using (var client = new ImapClient())\n        {\n            // Connect to the IMAP server\n            client.Connect(imapServer, port, SecureSocketOptions.SslOnConnect);\n\n            // Authenticate the user\n            client.Authenticate(username, password);\n\n            // Select the inbox folder\n            client.Inbox.Open(FolderAccess.ReadOnly);\n\n            // Search for new emails\n            var uids = client.Inbox.Search(SearchQuery.New);\n\n            // Fetch the new emails\n            foreach (var uid in uids)\n            {\n                var message = client.Inbox.GetMessage(uid);\n                Console.WriteLine($"New email from {message.From}: {message.Subject}");\n            }\n\n            // Disconnect from the IMAP server\n            client.Disconnect(true);\n        }\n    }\n}