using System;\nusing System.Web;\nusing System.Web.Services;\n\nnamespace YourNamespace\n{\n    public class UserControlHandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            // Handle AJAX requests here\n            string input = context.Request["input"];\n            List<string> list = new List<string>();\n\n            // Call the method on the page that will handle the lookup\n            HandleLookup(input, ref list);\n\n            // Return the result as JSON\n            context.Response.ContentType = "application/json";\n            context.Response.Write(JsonConvert.SerializeObject(list));\n        }\n\n        public bool IsReusable\n        {\n            get { return false; }\n        }\n\n        private void HandleLookup(string input, ref List<string> list)\n        {\n            // Call the method on the page that will handle the lookup\n            // You can use reflection or a interface to call the method on the page\n            // For example:\n            var page = HttpContext.Current.Handler as YourPageType;\n            page.HandleLookup(input, ref list);\n        }\n    }\n}