using RazorEngine;\nusing RazorEngine.Templating;\n\n// Define the report template\nstring template = @"\n<html>\n  <table border=""1"">\n    <tr>\n      <th>Date</th>\n      <th>Id</th>\n      <th>WorkItemId</th>\n      <th>Account Number</th>\n      <th>Name</th>\n      <th>Address</th>\n      <th>Description</th>\n    </tr>\n    @foreach (var item in Model)\n    {\n      <tr>\n        <td>@item.StartDate</td>\n        <td>@item.Id</td>\n        <td>@item.WorkItemId</td>\n        <td>@item.AccountNumber</td>\n        <td>@item.FirstName @item.LastName</td>\n        <td>@item.Address</td>\n        <td>@item.Description</td>\n      </tr>\n    }\n  </table>\n</html>";\n\n// Create a model for the report data\npublic class ReportModel\n{\n  public List<Item> Items { get; set; }\n}\n\n// Generate the report\nReportModel model = new ReportModel { Items = items };\nstring report = Engine.Razor.RunCompile(template, "report", typeof(ReportModel), model);