public class CustomSessionIDManager : SessionIDManager\n{\n    public override string CreateSessionID(HttpContext context)\n    {\n        if (context.Request.Url.Scheme == "https")\n        {\n            // Generate a session ID for HTTPS\n            return base.CreateSessionID(context) + "_HTTPS";\n        }\n        else\n        {\n            // Generate a session ID for HTTP\n            return base.CreateSessionID(context) + "_HTTP";\n        }\n    }\n}