using System.Security.Cryptography;\nusing System.Text;\n\n// Example encryption method\npublic static string Encrypt(string plainText)\n{\n    using (Aes aes = Aes.Create())\n    {\n        aes.Key = // generate or load a secure key\n        aes.IV = // generate or load a secure initialization vector\n\n        ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n        byte[] encryptedBytes = encryptor.TransformFinalBlock(Encoding.UTF8.GetBytes(plainText), 0, plainText.Length);\n\n        return Convert.ToBase64String(encryptedBytes);\n    }\n}