// appsettings.json ( encrypted )\n{\n  "SqlQueries": {\n    "GetUsers": "SELECT * FROM Users WHERE IsActive = @IsActive",\n    "GetOrders": "SELECT * FROM Orders WHERE CustomerId = @CustomerId"\n  }\n}\n\n// In your codebehind\nusing Microsoft.Extensions.Configuration;\n\npublic class MyController\n{\n    private readonly IConfiguration _configuration;\n\n    public MyController(IConfiguration configuration)\n    {\n        _configuration = configuration;\n    }\n\n    public IActionResult GetUsers(bool isActive)\n    {\n        string query = _configuration["SqlQueries:GetUsers"];\n        // Use parameterized query to avoid SQL injection\n        SqlCommand cmd = new SqlCommand(query, connection);\n        cmd.Parameters.AddWithValue("@IsActive", isActive);\n        // Execute query and retrieve data\n    }\n}