// Configure Windows authentication in your WCF service config\n<bindings>\n  <basicHttpBinding>\n    <security mode="TransportCredentialOnly">\n      <transport clientCredentialType="Windows" />\n    </security>\n  </basicHttpBinding>\n</bindings>\n\n// In your service code, access the ServiceSecurityContext\nusing System.Security.Principal;\nusing System.ServiceModel;\n\npublic class MyService : IMyService\n{\n    public void MyMethod()\n    {\n        IPrincipal principal = ServiceSecurityContext.Current.WindowsIdentity;\n        // Use the principal as needed\n    }\n}