// System metric constants\npublic enum SystemMetric : int\n{\n    SM_CXSCREEN = 0,  // Width of the screen in pixels\n    SM_CYSCREEN = 1,  // Height of the screen in pixels\n    SM_CXVSCROLL = 2,  // Width of a standard vertical scroll bar\n    SM_CYHSCROLL = 3,  // Height of a standard horizontal scroll bar\n    SM_CYCAPTION = 4,  // Height of a caption area\n    SM_CXBORDER = 5,  // Width of a window border\n    SM_CYBORDER = 6,  // Height of a window border\n    SM_CXFIXEDFRAME = 7,  // Width of a window frame that cannot be resized\n    SM_CYFIXEDFRAME = 8,  // Height of a window frame that cannot be resized\n    SM_CYVTHUMB = 9,  // Height of the thumb box in a vertical scroll bar\n    SM_CXHTHUMB = 10,  // Width of the thumb box in a horizontal scroll bar\n    SM_CXICON = 11,  // Width of an icon\n    SM_CYICON = 12,  // Height of an icon\n    SM_CXCURSOR = 13,  // Width of a cursor\n    SM_CYCURSOR = 14,  // Height of a cursor\n    SM_CYMENU = 15,  // Height of a menu\n    SM_CYMENUCHECK = 16,  // Height of a check box in a menu\n    SM_CYMENUSEPARATOR = 17,  // Height of a menu separator\n    SM_CXFULLSCREEN = 18,  // Width of the client area for a full-screen window\n    SM_CYFULLSCREEN = 19,  // Height of the client area for a full-screen window\n    SM_CYKANJIWINDOW = 20,  // Height of the Kanji window at the bottom of the screen\n    SM_MOUSEPRESENT = 21,  // Is a mouse installed?\n    SM_CYVSCROLL = 22,  // Height of a scroll bar arrow\n    SM_CXHSCROLL = 23,  // Width of a scroll bar arrow\n    SM_DEBUG = 24,  // Is the debug version of the system installed?\n    SM_SWAPBUTTON = 25,  // Are the mouse buttons swapped?\n    SM_RESERVED1 = 26,  // Reserved for future use\n    SM_RESERVED2 = 27,  // Reserved for future use\n    SM_RESERVED3 = 28,  // Reserved for future use\n    SM_RESERVED4 = 29,  // Reserved for future use\n    SM_CXMIN = 30,  // Minimum width of a window\n    SM_CYMIN = 31,  // Minimum height of a window\n    SM_CXSIZE = 32,  // Width of a window sizing border\n    SM_CYSIZE = 33,  // Height of a window sizing border\n    SM_CXFRAME = 34,  // Width of a window frame\n    SM_CYFRAME = 35,  // Height of a window frame\n    SM_CXMINTRACK = 36,  // Minimum width of a window tracking rectangle\n    SM_CYMINTRACK = 37,  // Minimum height of a window tracking rectangle\n    SM_CXDOUBLECLK = 38,  // Width of a rectangle around the location of the first click of a double-click\n    SM_CYDOUBLECLK = 39,  // Height of a rectangle around the location of the first click of a double-click\n    SM_CXICONSPACING = 40,  // Width of the space between icons\n    SM_CYICONSPACING = 41,  // Height of the space between icons\n    SM_MENUDROPALIGNMENT = 42,  // Alignment value of pop-up menus\n    SM_PENWINDOWS = 43,  // Is the Microsoft Windows for Pen Computing extensions installed?\n    SM_DBCSENABLED = 44,  // Is the double-byte character set (DBCS) version of the system installed?\n    SM_CMOUSEBUTTONS = 45,  // Number of buttons on the mouse\n    SM_SECURE = 46,  // Is security present?\n    SM_CXEDGE = 47,  // Width of a 3D border\n    SM_CYEDGE = 48,  // Height of a 3D border\n    SM_CXMINSPACING = 49,  // Minimum width of a window\n    SM_CYMINSPACING = 50,  // Minimum height of a window\n    SM_CXSMICON = 51,  // Width of a small icon\n    SM_CYSMICON = 52,  // Height of a small icon\n    SM_CYSMCAPTION = 53,  // Height of a small caption\n    SM_CXSMSIZE = 54,  // Width of small size of a window\n    SM_CYSMSIZE = 55,  // Height of small size of a window\n    SM_CXMENUSIZE = 56,  // Width of menu bar buttons\n    SM_CYMENUSIZE = 57,  // Height of menu bar buttons\n    SM_ARRANGE = 58,  // Is the taskbar always on top?\n    SM_CXMINIMIZED = 59,  // Width of a minimized window\n    SM_CYMINIMIZED = 60,  // Height of a minimized window\n    SM_CXMAXTRACK = 61,  // Maximum width of a window tracking rectangle\n    SM_CYMAXTRACK = 62,  // Maximum height of a window tracking rectangle\n    SM_CXMAXIMIZED = 63,  // Width of a maximized window\n    SM_CYMAXIMIZED = 64,  // Height of a maximized window\n    SM_NETWORK = 65,  // Is a network present?\n    SM_CLEANBOOT = 66,  // Is the system started in Safe Mode?\n    SM_CXDRAG = 67,  // Width of a rectangle around the location of the first click of a drag operation\n    SM_CYDRAG = 68,  // Height of a rectangle around the location of the first click of a drag operation\n    SM_SHOWSOUNDS = 69,  // Are sounds for menu and other system events present?\n    SM_CXMENUCHECK = 70,  // Width of menu check mark bitmap\n    SM_CYMENUCHECK = 71,  // Height of menu check mark bitmap\n    SM_SLOWMACHINE = 72,  // Is the computer a slow machine?\n    SM_MIDEASTENABLED = 73,  // Is the Middle East enabled?\n    SM_MOUSEWHEELPRESENT = 74,  // Is a mouse wheel present?\n    SM_XVIRTUALSCREEN = 75,  // Width of the virtual screen\n    SM_YVIRTUALSCREEN = 76,  // Height of the virtual screen\n    SM_CXVIRTUALSCREEN = 77,  // Width of the virtual screen\n    SM_CYVIRTUALSCREEN = 78,  // Height of the virtual screen\n    SM_CMONITORS = 79,  // Number of display monitors on the desktop\n    SM_SAMEDISPLAYFORMAT = 80,  // Are all the display monitors the same display format?\n    SM_IMMENABLED = 81,  // Is the Microsoft Input Method Manager (IMM) enabled?\n    SM_CXFOCUSBORDER = 82,  // Width of the focus rectangle\n    SM_CYFOCUSBORDER = 83,  // Height of the focus rectangle\n    SM_TABLETPC = 84,  // Is the Microsoft Windows XP Tablet PC edition installed?\n    SM_MEDIACENTER = 85,  // Is the Microsoft Windows XP Media Center edition installed?\n    SM_STARTER = 86,  // Is the Microsoft Windows XP Starter edition installed?\n    SM_SERVERR2 = 87,  // Is the Microsoft Windows Server 2003 R2 installed?\n    SM_MOUSEHORIZONTALWHEELPRESENT = 88,  // Is a mouse horizontal wheel present?\n    SM_CXPADDEDBORDER = 89,  // Width of the padded border\n    SM_REMOTESESSION = 4096,  // Is the session remote?\n}