using System.Text.RegularExpressions;\n\n// ...\n\nstring editorContent = "your content here";\nstring regExString = @"<a\b[^<>]*href\s*=\s*[\""\'](?<domain>https?:\/\/[^\/\s\'\""]*)*\/?(?<path>\/?[^\s\""]+?)?[[>\""\']";\nstring myInternalDomain = "your internal domain here";\n\nMatchEvaluator evaluator = new MatchEvaluator(match =>\n{\n    string domain = match.Groups["domain"].Value;\n    string path = match.Groups["path"].Value;\n\n    if (domain != myInternalDomain && domain != "")\n    {\n        // External domain, add javascript code to onclick attribute\n        return match.Value.Replace(">", $" onclick=\"your javascript code here\" target=\"_blank\">");\n    }\n    else if (path.EndsWith(".pdf"))\n    {\n        // PDF file, add target="_blank" attribute\n        return match.Value.Replace(">", $" target=\"_blank\">");\n    }\n    else\n    {\n        return match.Value;\n    }\n});\n\nstring modifiedContent = Regex.Replace(editorContent, regExString, evaluator);