public class HtmlModifier : IHttpModule\n{\n    public void Init(HttpApplication context)\n    {\n        context.EndRequest += (sender, e) =>\n        {\n            var response = ((HttpApplication)sender).Response;\n            var html = response.Output.ToString();\n            // Perform your string manipulations on the html variable\n            html = html.Replace("old text", "new text");\n            response.Clear();\n            response.Write(html);\n        };\n    }\n\n    public void Dispose()\n    {\n    }\n}