using System;\n\npublic static class BirthdayCalculator\n{\n    public static DateTime NextBirthday(DateTime birthDate, DateTime today)\n    {\n        var nextBirthday = new DateTime(today.Year, birthDate.Month, birthDate.Day);\n\n        if (birthDate.Month == 2 && birthDate.Day == 29)\n        {\n            nextBirthday = new DateTime(today.Year, 3, 1);\n        }\n\n        if (nextBirthday <= today)\n        {\n            nextBirthday = nextBirthday.AddYears(1);\n        }\n\n        if (DateTime.IsLeapYear(nextBirthday.Year) && birthDate.Month == 2 && birthDate.Day == 29)\n        {\n            nextBirthday = nextBirthday.AddDays(-1);\n        }\n\n        return nextBirthday;\n    }\n}