// Example using C# and parameterized queries\nstring input = "YO21 5EA";  // or "YORK"\nstring query = "";\nif (Regex.IsMatch(input, "^[A-Z]{1,2}[0-9R][0-9A-Z]? [0-9][ABD-HJLNP-UW-Z]{2}$"))  // postcode regex\n{\n    query = "SELECT * FROM your_table WHERE postcode = @input";\n}\nelse\n{\n    query = "SELECT * FROM your_table WHERE place = @input";\n}\n\nusing (SqlConnection connection = new SqlConnection(your_connection_string))\n{\n    connection.Open();\n    SqlCommand command = new SqlCommand(query, connection);\n    command.Parameters.AddWithValue("@input", input);\n    SqlDataReader reader = command.ExecuteReader();\n    // Process the results\n}