using System.DirectoryServices;\n\npublic bool IsADServerAlive(string ldapPath)\n{\n    try\n    {\n        using (var entry = new DirectoryEntry(ldapPath))\n        {\n            // Try to bind to the directory entry\n            entry.RefreshCache();\n            return true;\n        }\n    }\n    catch (DirectoryServicesCOMException)\n    {\n        // If we get here, the server is not responding\n        return false;\n    }\n}\n\n// Example usage:\nstring ldapPath = "LDAP://your-ad-server-name";\nif (IsADServerAlive(ldapPath))\n{\n    Console.WriteLine("AD server is alive");\n}\nelse\n{\n    Console.WriteLine("AD server is not responding");\n}